from gnuradio import gr, gr_unittest
from gnuradio import blocks
from gnuradio.filter import firdes
import pmt
import time
try:
    from gnuradio import pdu
except ImportError:
    import os
    import sys
    dirname, filename = os.path.split(os.path.abspath(__file__))
    sys.path.append(os.path.join(dirname, "bindings"))
    from gnuradio import pdu

class qa_pdu_frequency_modulator (gr_unittest.TestCase):

    def setUp (self):
        self.tb = gr.top_block ()
        self.gfsk = pdu.pdu_frequency_modulator_fc(0.5, firdes.gaussian(1,4,0.35,9))
        self.debug = blocks.message_debug()
        self.tb.msg_connect((self.gfsk, 'pdus'), (self.debug, 'store'))

    def tearDown (self):
        self.tb = None

